void socShutdown() {
		socExit();

}
size_t CurlWrite_CallbackFunc_StdString(void *contents, size_t size, size_t nmemb, std::string *s)
{
    size_t newLength = size*nmemb;

    s->append((char*)contents, newLength);


    return newLength;
}

void pSet(u8* screen, u32 x,u32 y,u8 r,u8 g, u8 b)
{
u32 color = RGB8_to_565(r, g, b);
int idx = (x * 240) + (239 - y);
((u16*)screen)[idx] = (u16)color;
} 

void drawText(u8* screen, u32 x, u32 y, u8 r, u8 b, u8 g)
{
	gspWaitForVBlank();
	for (int yclr = 0; yclr < 240; yclr++) {
		for (int xclr = 0; xclr < 340; xclr++) {
				pSet(screen,xclr,yclr,255,255,255);
		}
	}

	int stop=0;// the for loops below will stop when this is 1
	int c=-1;// the character to get from the page
	int yoffs=-(6*scroll)+8; //y pos of characters
	int xoffs=4; //x pos of characters

	
	while(stop==0 && c<8000) //draw the main text area of the browser display.
	{
	   	c++;
		int p = (int(text[c])-1)*24;
		if((int(text[c]))==10)
		{
		yoffs=yoffs+6;
		xoffs=0;
		}
		if((int(text[c]))==0){break;}

		if((int(text[c])!=10) && ((int(text[c])>31) || ctrlchar==1))
		{
			for (int yp = 0; yp < 6; yp++) {
				for (int xp = 0; xp < 4; xp++) {


					if(font[p] == '1' && xp+x+xoffs<340 && yp+yoffs>=0 ) {
						pSet(screen,xp+x+xoffs,yp+y+yoffs,r,g,b);
					}
					p++;
				}
			}
			xoffs=xoffs + 4;
		}

	if(yoffs>240)
	{
	stop=1;
	}

	}
 
 		for (int yclr = 0; yclr < 7; yclr++) {
		for (int xclr = 0; xclr < 340; xclr++) {
				pSet(screen,xclr,yclr,1255-(18*yclr),255-(18*yclr),255);
		}
	}
 
 	stop=0;
	c=-1;
	xoffs=0;
	while(stop==0 && c<8000) //draw the titlebar area of the browser display.
	{
	   	c++;
		int p = (int(mybuf[c])-1)*24;
		if((int(mybuf[c]))==0)
		{
		break;
		}

		if((int(mybuf[c])!=10) && ((int(mybuf[c])>31) || ctrlchar==1))
		{
			for (int yp = 0; yp < 6; yp++) {
				for (int xp = 0; xp < 4; xp++) {


					if(font[p] == '1' && xp+x+xoffs<340) {
						pSet(screen,xp+x+xoffs,yp+y,r,g,b);
					}
					p++;
				}
			}
			xoffs=xoffs + 4;
		}


	}

	

}



	
void drawDisp()
{
	u8* bscr = gfxGetFramebuffer(GFX_BOTTOM, GFX_LEFT, NULL, NULL);
	
	drawText(bscr,0,0,0,0,0);
	gfxFlushBuffers();
}














